    handleChange(e) {
      [name, value] = e.target;
        this.setState({
            [name]: value,
        })

    }

    render() {

      return (
        <div className="">
        <reactbootstrap.Container className="pt-5 px-0">
          <reactbootstrap.Form style={{ width: '600px', height: '200px'}}>
            <div className="col-md-12 row p-0">
                <div style={{color: '#EC661C'}} className="col-md-2">
                    <reactbootstrap.Form.Label>{t('Name:')}<span style={{color: 'red'}}>*</span></reactbootstrap.Form.Label>
                </div>
                <div className="col-md-10 pr-0">
                    <reactbootstrap.Form.Group>
                        <reactbootstrap.Form.Control type={'text'} placeholder={t('Name')} value={state.name} onChange={(e) => handleChange(e)}></reactbootstrap.Form.Control>
                        <span style={{ color: 'red' }}>{}</span>
                    </reactbootstrap.Form.Group>
                </div>
              </div>
            <div>
              <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleSubmit(e)}>
                    {'Save'}
                </reactbootstrap.Button>
            </div>
          </reactbootstrap.Form>
        </reactbootstrap.Container>
      </div>
      );
    }
}

export default (TasksManager);
