import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import DatePicker from 'react-datepicker';

class TasksManager extends Component {
    constructor(props) {
        super(props)
        this.state = {
          taskName: '',
          taskNameError: false,
          date: '',
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    handleSubmit(e) {
      if (this.state.taskName !== '') {
        let existingTasks = this.props.tasks
        this.props.handleTasks(e, existingTasks);
        console.log(this.props.tasks);
        // const localTasks = localStorage.getItem('tasks');
        // console.log(localTasks);
        //
        this.props.tasks.map((item) => {
          console.log(item);
        })
        // Object.keys(localTasks).map((item) => {
        //   console.log(item);
        // })
      } else {
        this.setState({
            taskNameError: true,
        })
      }

    }

    handleChange(e) {
      const {name, value} = e.target;
        this.setState({
            [name]: value,
            taskNameError: false,
        })
    }

    handleDate(date) {
      console.log(date);
    }

    render() {
      const { taskName, taskNameError } = this.state

      return (
        <div className="">
        <reactbootstrap.Container className="pt-5 px-0">
          <div style={{ color: '#EC661C', fontSize: '20px'}} >
              <span><h4>{'Create task'}</h4></span>
          </div>
          <reactbootstrap.Form style={{ width: '600px', height: '200px'}}>
            <reactbootstrap.FormGroup>
              <div style={{ marginbottom: '15px',border: '0px' }}>
                <label style={{ color: '#EC661C', fontSize: '14px' }}>{'Task name'}<span style={{ color: 'red' }}> * </span></label>
                <input
                  name="taskName"
                  value={taskName}
                  type="text"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px' }}
                  id="exampleInputPassword1"
                  placeholder={"TaskName"}
                  onChange={this.handleChange}
                  autoComplete="off" />
              </div>
              {taskNameError === true &&
                <div style={{ color: 'red', fontSize: '15px' }}>{"Task name is required"}</div>
              }
            </reactbootstrap.FormGroup>
            <DatePicker
                selected={this.state.date}
                onChange={(date) => this.handleDate(date)}
                showTimeSelect
                timeFormat="HH:mm"
                timeIntervals={15}
                timeCaption="time"
                dateFormat="yyyy/M/d h:mm aa"
            />
            <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleSubmit(e)}>
                  {'Save'}
            </reactbootstrap.Button>
          </reactbootstrap.Form>
        </reactbootstrap.Container>
      </div>
      );
    }
}

export default (TasksManager);
