import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import { Constants } from '../_components/Constants';
import CreateTask from './Tasks/CreateTask'

class TasksManager extends Component {
    constructor(props) {
        super(props)
        this.state = {
          Tasks: Constants.Tasks
        }
    }

    createtask() {
      return (
        <div className="App">
          <header className="App-header">
              <BrowserRouter>
                <Switch>
                  <Route exact path='/create' component={CreateTask} />
                </Switch>
              </BrowserRouter>
          </header>
        </div>
      );
      // this.state = {
      //   showCreateTask: Constants.Tasks
      // }
    }


    render() {
      const { Tasks } = this.state;

      return (
        <div className="">
        <reactbootstrap.Container className="pt-5 px-0">
          <div>
            <reactbootstrap.Button style={{ float: 'left',backgroundColor: '#EC661C',borderColor: "black", width: '80px' }} type="button" onClick={this.createtask}>
                  {'Create task'}
              </reactbootstrap.Button>
          </div>
          <div style={{ color: '#EC661C', fontSize: '20px'}} >
              <span><h4>{'Tasks'}</h4></span>
          </div>

          <reactbootstrap.Form style={{ width: '600px', height: '200px'}}>
            <reactbootstrap.Table style={{ display: 'block', border: '2px solid lightgray'}}>
              <thead style={{ backgroundColor: '#EC661C', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                  <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                    <th>{'No'}</th>
                    <th>{'Task name'}</th>
                    <th>{'Start time'}</th>
                    <th>{'End time'}</th>
                    <th>{'Edit'}</th>
                    <th>{'Delete'}</th>
                  </tr>
                </thead>
                <tbody style={{ backgroundColor: 'gray', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                  {Tasks.length > 0 &&
                    <>
                      {Tasks.map((item) => (
                        <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                           <td>{item}</td>
                           <td>{item}</td>
                           <td>{item}</td>
                           <td>{item}</td>
                           <td>{item}</td>
                        </tr>
                      ))}
                    </>
                  }
                </tbody>
            </reactbootstrap.Table>
          </reactbootstrap.Form>
        </reactbootstrap.Container>
      </div>
      );
    }
}

export default (TasksManager);
