import React, { Component } from 'react'
import * as reactbootstrap from 'react-bootstrap'
import { Constants } from '../_components/Constants'
import CreateTask from './CreateTask'
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'

class TasksManager extends Component {
    constructor(props) {
        super(props)
        this.state = {
          Tasks: (this.props.tasks !== undefined) ? this.props.tasks : Constants.Tasks,
        }
    }

    // componentDidMount() {
    //   let localTasks = localStorage.getItem('AllTasks');
    //   console.log(this.props.tasks);
    //   Object.values(this.props.tasks).map((item) => {
    //     console.log(item);
    //   })
    // }


    handleEdit(e, index) {
      this.props.handleEdit(index);
    }

    handleDelete(e, index) {

    }

    // createtask() {
    //   return (
    //     <div className="App">
    //       <header className="App-header">
    //           <BrowserRouter>
    //             <Switch>
    //               <Route exact path='/create' component={CreateTask} />
    //             </Switch>
    //           </BrowserRouter>
    //       </header>
    //     </div>
    //   );
    //   // this.state = {
    //   //   showCreateTask: Constants.Tasks
    //   // }
    // }


    render() {
      const { Tasks } = this.state;

      return (
        <div className="">
        <reactbootstrap.Container className="pt-5 px-0">
          <div style={{ color: '#EC661C', fontSize: '20px'}} >
              <span><h4>{'Tasks'}</h4></span>
          </div>

          <reactbootstrap.Form style={{ width: '600px', height: '200px'}}>
            <reactbootstrap.Table style={{ display: 'block', border: '2px solid lightgray'}}>
              <thead style={{ backgroundColor: '#EC661C', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                  <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                    <th>{'Task name'}</th>
                    <th>{'Start time'}</th>
                    <th>{'End time'}</th>
                    <th>{'Edit'}</th>
                    <th>{'Delete'}</th>
                  </tr>
                </thead>
                <tbody style={{ backgroundColor: 'gray', color: 'white', position: 'sticky', top: '0', textAlign: 'center' }}>
                  {Tasks.length > 0 &&
                    <>
                      {Object.values(Tasks).map((index, item) => (
                        <tr style={{ textAlign: 'center', border: '2px solid black' }}>
                           <td>{item.name}</td>
                           <td>{item.startDate}</td>
                           <td>{item.endDate}</td>
                           <td>
                             <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleEdit(e, index)}>
                                   {'Edit'}
                             </reactbootstrap.Button>
                           </td>
                           <td>
                             <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleDelete(e, index)}>
                                   {'Delete'}
                             </reactbootstrap.Button>
                           </td>
                        </tr>
                      ))}
                    </>
                  }
                </tbody>
            </reactbootstrap.Table>
          </reactbootstrap.Form>
        </reactbootstrap.Container>
      </div>
      );
    }
}

export default (TasksManager);
