import React, { Component } from 'react'
import './App.css'
import CreateTask from './Tasks/CreateTask'
import TasksManager from './Tasks/TasksManager'
import { Tabs, Tab } from 'react-bootstrap'
import * as reactbootstrap from 'react-bootstrap';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      active_tab: 1,
    }

    this.handleSelect = this.handleSelect.bind(this);
  }

  handleSelect(key) {
      this.setState({
          active_tab: key,
      });
  }

  render() {
    const { active_tab } = this.state
    console.log(active_tab);

    return (
      <div className="App">
        <header className="App-header">
          <reactbootstrap.Container className="p-1">
            <reactbootstrap.Form >
              <reactbootstrap.Tabs id="controlled-tab-example" activeKey={active_tab} onSelect={(e) => this.handleSelectTab(e)}>
                <reactbootstrap.Tab eventKey={1} title={"Create task"}>
                  <CreateTask />
                </reactbootstrap.Tab>
                &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;
                <reactbootstrap.Tab eventKey={2} title={'Manage tasks'}>
                  <TasksManager />
                </reactbootstrap.Tab>
              </reactbootstrap.Tabs>
            </reactbootstrap.Form>
          </reactbootstrap.Container>
        </header>
      </div>
    );
  }
}

export default App;
