import React, { Component } from 'react'
import './App.css'
import CreateTask from './Tasks/CreateTask'
// import TaskMainpage from './Tasks/TaskMainpage'
import { BrowserRouter, Route, Switch, Redirect } from 'react-router-dom'
import { Constants } from './_components/Constants';
import TasksManager from './Tasks/TasksManager'
import * as reactbootstrap from 'react-bootstrap'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      tasks: Constants.Tasks,
      active_tab: 1,
    }

    // this.handleSelect = this.handleSelect.bind(this);
  }

  // handleSelect(key) {
  //     this.setState({
  //         active_tab: key,
  //     });
  // }

  render() {
    // localStorage.setItem('tasks', this.state.tasks.allTasks);

    localStorage.setItem('tasks', this.state.tasks.allTasks);
    // const NotFoundRedirect = () => <Redirect to='/manageTasks' />

    // if (localUser !== undefined && localUser === null) {
      // return (
      //   <div className="App">
      //     <header className="App-header">
      //         <BrowserRouter>
      //           <Switch>
      //             <Route exact path='/manageTasks' component={TasksManager} />
      //             <Route component={NotFoundRedirect} />
      //           </Switch>
      //         </BrowserRouter>
      //     </header>
      //   </div>
      // );
    // } else {
    //   return (
    //     <div className="App">
    //       <header className="App-header">
    //           <BrowserRouter>
    //             <Switch>
    //               <Route exact path='/manageEmployees' {...history} component={EmployeeListPage} />
    //             </Switch>
    //           </BrowserRouter>
    //       </header>
    //     </div>
    //   );
    // }

    return (
      <div className="App">
        <header className="App-header">
          <div style={{display: 'flex' }}>
            <div>
              <reactbootstrap.Button style={{ float: 'left',backgroundColor: '#EC661C',borderColor: "black", width: '80px' }} type="button" onClick={this.createtask}>
                    {'Create task'}
                </reactbootstrap.Button>
            </div>
            &nbnp;
            <div>
              <reactbootstrap.Button style={{ float: 'left',backgroundColor: '#EC661C',borderColor: "black", width: '80px' }} type="button" onClick={this.manageTask}>
                    {'Manege task'}
                </reactbootstrap.Button>
            </div>
          </div>

          <div className='container py-2' >
            <div className='row justify-content-center' >
              <div className='col-lg-12 col-md-12 float-left px-0' >
                  <reactbootstrap.Container className="p-1">
                    <reactbootstrap.Form >
                      <reactbootstrap.Tabs id="controlled-tab-example" activeKey={this.state.active_tab} onSelect={(e) => this.handleSelect(e)}>
                        <reactbootstrap.Tab eventKey={1} title={"Create task"}>
                          <CreateTask />
                        </reactbootstrap.Tab>
                        &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;
                        <reactbootstrap.Tab eventKey={2} title={'Manage tasks'}>
                          <TasksManager />
                        </reactbootstrap.Tab>
                      </reactbootstrap.Tabs>
                    </reactbootstrap.Form>
                  </reactbootstrap.Container>
                </div>
              </div>
            </div>
        </header>
      </div>
    );
  }
}

export default App;
