import React, { Component } from 'react';
import * as reactbootstrap from 'react-bootstrap';
import DatePicker from 'react-datepicker';

class TasksManager extends Component {
    constructor(props) {
        super(props)
        this.state = {
          taskName: '',
          taskNameError: false,
          // startDate: undefined,
          // endDate: undefined,
          startDate: '',
          endDate: '',
        }
        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.handleDate = this.handleDate.bind(this)
    }

    handleSubmit(e) {
      if (this.state.taskName !== '') {
        let existingTasks = this.props.tasks
        this.props.handleTasks(e, existingTasks);
        console.log(this.props.tasks);
        console.log(this.props.startDate);
        console.log(this.props.endDate);
        // const localTasks = localStorage.getItem('tasks');
        // console.log(localTasks);
        //
        this.props.tasks.map((item) => {
          console.log(item);
        })
        // Object.keys(localTasks).map((item) => {
        //   console.log(item);
        // })
      } else {
        this.setState({
            taskNameError: true,
        })
      }

    }

    handleChange(e) {
      const {name, value} = e.target;
        this.setState({
            [name]: value,
            taskNameError: false,
        })
    }

    handleDate(e, type) {
      var today = new Date();
      const {name, value} = e.target;
      // console.log(e.target);
      // console.log(e.target.name);
      // console.log(e.target.value);
      var time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
      let timeStamp = e.target.value + ' ' + time;
      // console.log(timeStamp);
      // this.setState({
      //     [name]: timeStamp,
      // })
    }

    render() {
      const { taskName, taskNameError, startDate } = this.state

      return (
        <div className="">
        <reactbootstrap.Container className="pt-5 px-0">
          <div style={{ color: '#EC661C', fontSize: '20px'}} >
              <span><h4>{'Create task'}</h4></span>
          </div>
          <reactbootstrap.Form style={{ width: '600px', height: '200px'}}>
            <reactbootstrap.FormGroup>
              <div style={{ marginbottom: '15px',border: '0px' }}>
                <label style={{ color: '#EC661C', fontSize: '14px' }}>{'Task name'}<span style={{ color: 'red' }}> * </span></label>
                <input
                  name="taskName"
                  value={taskName}
                  type="text"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px' }}
                  id="exampleInputPassword1"
                  placeholder={"TaskName"}
                  onChange={this.handleChange}
                  autoComplete="off" />
              </div>
              {taskNameError === true &&
                <div style={{ color: 'red', fontSize: '15px' }}>{"Task name is required"}</div>
              }
            </reactbootstrap.FormGroup>
            <reactbootstrap.FormGroup>
              <div style={{ marginbottom: '15px',border: '0px' }}>
                <label style={{ color: '#EC661C', fontSize: '14px' }}>{'Start date'}<span style={{ color: 'red' }}> * </span></label>
                {/* <input
                  name="startDate"
                  value={this.state.startDate}
                  type="date"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px' }}
                  id="exampleInputPassword1"
                  placeholder={"Start date"}
                  onChange={(e) => this.handleDate(e, 'start')}
                  autoComplete="off" /> */}
                  <input
                      id="startDate"
                      type="date"
                      name="startDate"
                      placeholder={t("Birth date")}
                      value={startDate}
                      onChange={this.handleDate}
                      className="input_sw"
                  />
              </div>
            </reactbootstrap.FormGroup>
            <reactbootstrap.FormGroup>
              <div style={{ marginbottom: '15px',border: '0px' }}>
                <label style={{ color: '#EC661C', fontSize: '14px' }}>{'End date'}<span style={{ color: 'red' }}> * </span></label>
                <input
                  name="endDate"
                  value={this.state.endDate}
                  type="date"
                  style={{ marginBottom: '15px', width: '250px', borderColor: "black", borderWidth: '3px' }}
                  id="exampleInputPassword1"
                  placeholder={"End date"}
                  onChange={(e) => this.handleDate(e, 'end')}
                  autoComplete="off" />
              </div>
            </reactbootstrap.FormGroup>
            <reactbootstrap.Button style={{ float: 'right',backgroundColor: '#EC661C',borderColor: "black", marginBottom: '25px', marginTop: '25px', width: '80px' }} type="button" onClick={(e) => this.handleSubmit(e)}>
                  {'Save'}
            </reactbootstrap.Button>
          </reactbootstrap.Form>
        </reactbootstrap.Container>
      </div>
      );
    }
}

export default (TasksManager);
